#include <stdio.h>
void changeValue(int num) {
   num = 100;
   printf("Inside function: %d\n", num);
}
int main() {
   int x = 10;
   changeValue(x);
   printf("After call: %d\n", x);
   return 0;
}

#include <stdio.h>
void changeValue(int *num) {
   *num = 100;
   printf("Inside function: %d\n", *num);
}
int main() {
   int x = 10;
   changeValue(&x);
   printf("After call: %d\n", x);
   return 0;
}

