#include <stdio.h>
int main(){
   int marks[5] = {50, 60, 70, 80, 90};
   int sum=0;
   for(int i=0;i<5;i++){ sum += marks[i]; }
   float avg = (float)sum/5;
   printf("Average: %f", avg);
   return 0;
}

#include <stdio.h>
int main() {
   int matrix[2][3] = { {1,2,3}, {4,5,6} };
   for(int i=0;i<2;i++) {
      for(int j=0;j<3;j++) {
         printf("%d ", matrix[i][j]);
      }
      printf("\n");
   }
   return 0;
}

#include <stdio.h>
int main(){
   int a=10;
   int *ptr;
   ptr=&a;
   printf("a=%d\n", a);
   printf("Address=%p\n", ptr);
   printf("Value using pointer=%d", *ptr);
   return 0;
}

#include <stdio.h>
int main(){
   int arr[3] = {10,20,30};
   int *p = arr;
   for(int i=0;i<3;i++){
      printf("%d ", *(p+i));
   }
   return 0;
}
