#include <iostream>
using namespace std;

int main() {
   for (int i = 1; i <= 5; i++) {
      cout << i << " ";
   }
   return 0;
}

int i = 1;
while (i <= 5) {
   cout << "Number: " << i << endl;
   i++;
}

int i = 1;
do {
   cout << "Count = " << i << endl;
   i++;
} while (i <= 5);

for (int i = 0; i < 5; i++) {
   if (i == 3) break;
   cout << i << " ";
}

for (int i = 0; i < 5; i++) {
   if (i == 2) continue;
   cout << i << " ";
}

for (int i = 0; i < 5; i++) {
   if (i == 3) goto end;
   cout << i << " ";
}
end:
cout << "End";

